mapper.select:
SELECT COUNT(*) FROM product 


		and name like '%${name}%'


		and status = #{status}


		SELECT p.id,c.name AS category_name,p.name,p.subtitle,p.main_image,p.price,p.stock,p.status,p.createtime,p.updatetime
         FROM product AS p INNER JOIN  category AS c
         ON p.category_id=c.id



				and p.name like '%${name}%'


				and p.status = #{status}


		limit #{pageIndex},#{pageSize}

SELECT p.id,c.name AS category_name,p.name,p.subtitle,p.main_image,p.price,p.stock,p.status,p.createtime,p.updatetime FROM product AS p INNER JOIN category AS c ON p.category_id=c.id
and name like '%${name}%'
and status = #{status}
